class Animator {
    constructor (ctx, spriteSheet) {
        this.assets = {};
        this.ctx = ctx;
        spriteSheet.generateSprites(ctx).forEach(sprite => this.addAsset(sprite));
        // debugger;
        Object.values(this.assets).forEach((ctx, idx) => this.ctx.putImageData(idx * 32, 0, 32, 32));
    }

    addAsset (asset) {
        this.assets[asset.data] = asset;
    }

    deleteAsset (asset) {
        delete this.assets[asset];
    }
}

export default Animator;
