class ResourceManager {
    constructor () {
        this.resourceCache = {};
        this.loading = [];
        this.callbacks = [];
    }

    load (resource) {
        if (resource instanceof Array) {
            resource.forEach(res => this._load(res));
        } else this._load(resource);
    }

    _load (url) {
        if (this.resourceCache[url]) return this.resourceCache[url];
        else {
            const img = new Image();
            img.onload = () => {
                this.resourceCache[url] = img;
                if (this.isReady()) this.callbacks.forEach(cb => {debugger; cb()});
            }
            this.resourceCache[url] = false;
            img.src = url;
        }
    }

    get (url) {
        return this.resourceCache[url];
    }

    isReady () {
        let ready = true;
        Object.keys(this.resourceCache).forEach(k => {
            if (this.resourceCache.hasOwnProperty(k) && !(this.resourceCache[k]))
                ready = false;
        });
        return ready;
    }

    onReady (func) {
        this.callbacks.push(func);
    }
}

export default ResourceManager;
