class Animation {
    constructor (ctx, sprite, config) {
        this.ctx = ctx;
        this.sprite = sprite;
        this.frames = config.frames || 1;
        this.frameWidth = Math.floor(this.sprite.img.width / this.frames);
        this._index = 0;

        this.config = config || {
            loop: true
        }
        window.spritevel = this.sprite.velocity;
    }

    update (dt) {
        this._index = ++this._index % this.frames;
    }

    render (axis, dir) {
        // let frame;
        // if (this.sprite.speed > 0) {
        //     const maxFrameCount = this.frames.length;
        //     const nextFrameIdx = Math.floor(this._index);
        //     frame = this.frames[nextFrameIdx % maxFrameCount];

        //     if (!this.config.loop && nextFrameIdx > maxFrameCount) return;
        //     else frame = 0;
        // }
        if (dir > 0) {
            this._index = (this.sprite.velocity[axis] > 0) ? this._index : 0;
        } else {
            this._index = (this.sprite.velocity[axis] < 0) ? this._index : 0;
        }
        this.ctx.drawImage(this.sprite.img, this._index * this.sprite.size.width, 0, this.sprite.size.width, this.sprite.size.height, 0, 0, 48, 48);
    }
}

export default Animation;
