import { debug } from "util";

class Animator {
    constructor (ctx, spriteSheet) {
        this.assets = {};
        this.ctx = ctx;
        this.generateSprites(spriteSheet) //.forEach(sprite => this.addAsset(sprite));
    }

    generateSprites (spriteSheet) {
        // const output = [];
        const c = new OffscreenCanvas(spriteSheet.img.width, spriteSheet.img.height);
        const cc = c.getContext('2d');
        cc.drawImage(spriteSheet.img, 0, 0, 96, 96, 0, 0, 96, 96);
        let imgBM = createImageBitmap(spriteSheet.img, { premultiplyAlpha: 'premultiply' }).then((res) => {
            this.addAsset(res);
            // debugger;
        });
    }

    addAsset (asset) {
        this.assets[asset.data] = asset;
    }

    deleteAsset (asset) {
        delete this.assets[asset];
    }

    render () {
        // Object.values(this.assets).forEach((sprite, idx) => this.ctx.putImageData(sprite, idx*48, 64));
        this.ctx.drawImage(this.assets.undefined, 64, 64);
    }
}

export default Animator;
