class Sprite {
    constructor (canvas, ctx, img) {
        this.canvas = canvas;
        this.ctx = ctx;

        this.img = img;

        this.size = {
            width: 32,
            height: 32
        };

        this.position = {
            x: 0,
            y: 0
        }
    }

    render () {
        // debugger;
        this.ctx.drawImage(this.img, this.position.x, this.position.y, this.size.width, this.size.height, 0, 0, this.size.width, this.size.height);
    }
}

export default Sprite;
